import React, { Fragment } from 'react' 
import { Button } from '../../../elements';
import  './ImageMode.css'
import { setImageSide, setImages } from '../../../redux/actions/Actions';
import { useDispatch } from 'react-redux';
const ImageMode = (props) => {
  const {
    capture = ()=>{},
    _handleClick = ()=>{},
    imgSrc
  } = { ...props };   
      
  const dispatch = useDispatch();
      
      
      return (
        <Fragment>
          {/* camera and image */}
            <Fragment>
              {/* after click image */}
              <img src={imgSrc} alt="captured-image" />
            </Fragment>
            
          {/* instruction and button */}
          <div className="container video"> 
            <Button
              clsname={'capture-button'}
              title={ 'Re-capture' }
              onClick={()=>{
                 dispatch(setImageSide('selfie'));
                 dispatch(setImages(null));
                _handleClick();
              }}
            />
            <Button
              clsname={'capture-button'}
              title={ 'Submit' }
              onClick={capture}
            />
          </div>
        </Fragment>
      )
}

export default ImageMode